package java00;

public class VariablesDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//initializing variables : 
		int age = 25;
		float height = 5.9f;
		boolean isStudent = true;
		char grade = 'A';
		
		System.out.println("Boolean Type : "+isStudent);
		System.out.println("Integer Type : "+age);
		System.out.println("Float Type : "+height);
		System.out.println("Character Type : "+grade);
		
		//TypeCasting
		
		//implicit casting : Short to Long 
		//explicit casting : long to short 
		
		//ternary operator : expression? value1 : value2
	}

}
